    "SnapshotSerengetiS10.json",
    "SnapshotSerengetiS11.json"

var dataset = new SerengetiData();
var resetEvent = new ManualResetEvent(false);
int filesLeft = seasonFiles.Count;


Parallel.ForEach(seasonFiles, async file =>
{
    var blob = blobDirectory.GetBlockBlobReference(file);
    var data = await ReadJsonFileAsync<SerengetiData>(blob);

    lock (dataset)
    {
        if(seasonFiles.IndexOf(file)==1)
            dataset.Categories.AddRange(data.Categories);

        dataset.Annotations.AddRange(data.Annotations);
        dataset.Images.AddRange(data.Images);
    }

    if (Interlocked.Decrement(ref filesLeft) == 0)
    {
        resetEvent.Set();
    }
});

resetEvent.WaitOne();

